import React from 'react';
import App from './App';

function TodoList() {
    const [desc, setDesc] = React.useState('');
    const [date, setDate] = React.useState('')
    const [todos, setTodos] = React.useState([]);



    const inputChanged = (event) => {
        setDesc(event.target.value);

    }
    const inputChanged2 = (event) => {
        setDate(event.target.value);
    }
    const addTodo = (event) => {
        event.preventDefault();
        setTodos([...todos, { desc, date }])

        setDesc('');
        setDate('');

    }
    return (
        <div>
            <p class="groove"> <h1>My Todos:</h1>
                Description: <input type="text" onChange={inputChanged} value={desc} />
                Date: <input type="text" onChange={inputChanged2} value={date} />
                <button onClick={addTodo}>Add</button></p>
            <table><tbody>
                <tr><th>Date: </th><th>Description: </th></tr>
                {todos.map((todo, index) => <tr key={index}><td>{todo.date}</td><td>{todo.desc}</td></tr>)}
            </tbody></table>

        </div>
    )

};

export default TodoList;
